.body {
  background: #000;/*linear-gradient(#FFF, #999);*/
  cursor: none;
  /*
  transform: rotate(-3.5deg);
  zoom: 0.95;
  */
}
body.showing-lyrics {
  background: linear-gradient(45deg, #2E6A97 0%, #73B7E5 50%, #5BA7B3 100%);
  background-attachment: fixed;
}
.song-wrap {
  font-family: "Trebuchet MS";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  overflow: hidden;
  transition: 0.1s linear top;
}
.song-wrap > .heading-wrap {
  margin: 2em 3em;
  padding: 1em;
  background-color: rgba(255,255,255,0.2);
  border-radius: 1em;
  box-shadow: 0 0 0.1em 0.05em #000, inset 0.2em 0.2em 0.5em #FFF, inset -0.1em -0.1em 0.5em #000;
}
.song-wrap > .heading-wrap > .song-number {
  text-align: center;
  font-size: 4vw;
  font-weight: bold;
  color: #032B41;
  text-shadow: 0 0 0.2em, 0 0 0.1em #FFF, 0 0 0.1em #FFF, 0 0 0.1em #FFF;
}
.song-wrap > .heading-wrap > .title {
  text-align: center;
  font-size: 7vw;
  font-weight: bold;
  color: #8A3729;
  text-shadow: 0 0 0.2em, 0 0 0.1em #FFF, 0 0 0.1em #FFF, 0 0 0.1em #FFF;
}
.song-wrap > .heading-wrap > .theme-text {
  text-align: center;
  font-size: 3vw;
  color: #134561;
}
.song-wrap > .lyrics-table {
  display: table;
  padding: 0 1em;
  line-height: 2em;
  font-size: 100vw;
  font-family: "Trebuchet MS";
  box-sizing: border-box;
}
.song-wrap > .lyrics-table > .stanza {
  display: table-row;
}
.song-wrap > .lyrics-table > .stanza > .number-cell {
  display: table-cell;
  vertical-align: top;
  text-align: right;
}
.song-wrap > .lyrics-table > .stanza > .lines-cell {
  display: table-cell;
  vertical-align: top;
  padding-left: 0.5em;
  box-sizing: border-box;
}
.song-wrap > .lyrics-table > .stanza > .lines-cell > .line {
  box-sizing: border-box;
  white-space: nowrap;
}

return $(html).find('.completeList a[data-rsconf][data-lib]').map(function(i, e) {
  var j = $(e), jLang = j.find('[lang]');
  return {
    rsconf: j.data('rsconf'),         // eg. "r5"
    lib: j.data('lib'),               // eg. "lp-t"
    name: jLang.eq(0).text().trim(),  // eg. "Portuguese"
    lang: jLang.eq(1).text().trim()   // eg. "Português"
  };
}).toArray();

var j = $(html).find('.pub-sn, .pub-snnw');
var heading = j.find('.contextTtl').text().trim().replace(/\xA0/g, ' ');
var title = j.find('h1').text().trim().replace(/\xA0/g, ' ');
var theme = j.find('.themeScrp').text().trim().replace(/\xA0/g, ' ');
var stanzas = j.find('.bodyTxt [data-pid]').toArray().reduce(function(stanzas, e, i) {
  var j = $(e);
  if (j.hasClass('sl')) {
    stanzas.push([]);
  }
  [...stanzas].pop().push(j.text().trim().replace(/^\d\.[\xA0\s]+/, '').replace(/\xA0/g, ' '));
  return stanzas;
}, []);
return j[0] && { heading, title, theme, stanzas };

